import React, { createContext, useState, useEffect } from 'react';
import axios from 'axios';

export const AuthContext = createContext();

const API_URL = 'http://localhost:5000/api/auth';

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      verifyToken();
    } else {
      setLoading(false);
    }
  }, [token]);

  const verifyToken = async () => {
    try {
      const response = await axios.get(`${API_URL}/verify`);
      setUser(response.data.user);
    } catch (error) {
      console.error('Token verification failed:', error);
      logout();
    } finally {
      setLoading(false);
    }
  };

  const signup = async (name, email, password) => {
    console.log('=== AuthContext: SIGNUP FUNCTION CALLED ===');
    console.log('Name:', name);
    console.log('Email:', email);
    console.log('Password length:', password.length);

    try {
      const requestData = {
        username: name,  // Backend expects 'username'
        email,
        password
      };

      console.log('Sending request to:', `${API_URL}/signup`);
      console.log('Request data:', requestData);

      const response = await axios.post(`${API_URL}/signup`, requestData);

      console.log('=== SIGNUP RESPONSE RECEIVED ===');
      console.log('Status:', response.status);
      console.log('Data:', response.data);

      const { token: newToken, user: userData } = response.data;

      localStorage.setItem('token', newToken);
      setToken(newToken);
      setUser(userData);
      axios.defaults.headers.common['Authorization'] = `Bearer ${newToken}`;

      console.log('Signup successful! User:', userData);
      return response.data;
    } catch (error) {
      console.error('=== SIGNUP ERROR IN AUTHCONTEXT ===');
      console.error('Error status:', error.response?.status);
      console.error('Error data:', error.response?.data);
      console.error('Error message:', error.message);
      throw error;
    }
  };

  const login = async (email, password) => {
    console.log('=== AuthContext: LOGIN FUNCTION CALLED ===');
    console.log('Email:', email);
    console.log('Password length:', password.length);

    try {
      const requestData = { email, password };

      console.log('Sending request to:', `${API_URL}/login`);
      console.log('Request data:', requestData);

      const response = await axios.post(`${API_URL}/login`, requestData);

      console.log('=== LOGIN RESPONSE RECEIVED ===');
      console.log('Status:', response.status);
      console.log('Data:', response.data);

      const { token: newToken, user: userData } = response.data;

      localStorage.setItem('token', newToken);
      setToken(newToken);
      setUser(userData);
      axios.defaults.headers.common['Authorization'] = `Bearer ${newToken}`;

      console.log('Login successful! User:', userData);
      return response.data;
    } catch (error) {
      console.error('=== LOGIN ERROR IN AUTHCONTEXT ===');
      console.error('Error status:', error.response?.status);
      console.error('Error data:', error.response?.data);
      console.error('Error message:', error.message);
      throw error;
    }
  };

  const logout = () => {
    console.log('=== LOGGING OUT ===');
    localStorage.removeItem('token');
    setToken(null);
    setUser(null);
    delete axios.defaults.headers.common['Authorization'];
  };

  return (
    <AuthContext.Provider value={{ user, token, signup, login, logout, loading }}>
      {children}
    </AuthContext.Provider>
  );
};
